// Firebase
import firebase from 'firebase/compat/app';
import 'firebase/compat/storage';
import { getAuth, GoogleAuthProvider, onAuthStateChanged, signInWithPopup } from 'firebase/auth';
// Classes
import { User } from '../classes/user';
import { BehaviorSubject } from "rxjs";
import { doc, setDoc } from "firebase/firestore";
import * as i0 from "@angular/core";
import * as i1 from "./database.service";
import * as i2 from "./user.service";
import * as i3 from "@angular/router";
export class AuthenticationService {
    constructor(databaseService, userService, router) {
        this.databaseService = databaseService;
        this.userService = userService;
        this.router = router;
        this.m_user_b_subject = new BehaviorSubject(this.m_user);
    }
    async init_authentication() {
        this.get_database_on_auth((user) => {
            if (user) {
                this.m_uid = user.uid;
                this.m_user_snapshot = this.databaseService.m_data_base
                    .collection(this.databaseService.m_collections.users).doc(user.uid).get()
                    .then((r_user) => {
                    this.m_user = r_user;
                    this.m_user_b_subject.next(this.m_user);
                    if (!this.m_user)
                        this.m_user = new User(user.providerData[0].displayName, user.providerData[0].email, user.providerData[0].photoURL, user.providerData[0].providerId);
                    // this.userService.save_firestore_user( this.userService.m_user, this.m_uid)
                    this.m_user_b_subject.next(this.m_user);
                });
            }
            else {
                this.router.navigate(['game/home']);
            }
        });
    }
    async save_firestore_user(r_user, r_uid) {
        await setDoc(doc(this.databaseService.m_data_base, this.databaseService.m_collections.users, r_uid), Object.assign({}, r_user));
    }
    get_database_on_auth(m_function) {
        const auth = getAuth();
        return onAuthStateChanged(auth, m_function);
    }
    get_database_auth() {
        this.databaseService.database_init();
        return firebase.auth();
    }
    sign_in_google() {
        const auth = getAuth();
        const provider = new GoogleAuthProvider();
        signInWithPopup(auth, provider).then(result => {
            this.router.navigate(['game/home']);
        }).catch((error) => {
            console.log(error);
        });
    }
    logout() {
        this.router.navigate(['authentication/log-in']);
        return this.get_database_auth().signOut();
    }
}
