    constructor(databaseService) {
        this.databaseService = databaseService;
    }
    async get_firestore_user(r_uid) {
        const user_snap = await getDoc(doc(this.databaseService.m_data_base, this.databaseService.m_collections.users, r_uid));
        if (user_snap.exists())
            this.m_user = user_snap.data();
        return true;
    }
    async save_firestore_user(r_user, r_uid) {
        await setDoc(doc(this.databaseService.m_data_base, this.databaseService.m_collections.users, r_uid), Object.assign({}, r_user));
    }
}
