import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { collection, getDocs } from "firebase/firestore";
let GameHistoryComponent = class GameHistoryComponent {
    constructor(databaseService, historyService) {
        this.databaseService = databaseService;
        this.historyService = historyService;
        this.m_array_history = new Array();
        this.m_array_history_observable = this.historyService.m_history_b_subject.asObservable();
        // this.get_firestore_history()
    }
    ngOnInit() {
    }
    ngOnDestroy() {
        this.historyService._finish();
    }
    async get_firestore_history() {
        const m_history_snapshot = await getDocs(collection(this.databaseService.m_data_base, this.databaseService.m_collections.history));
        m_history_snapshot.forEach((doc) => {
            console.log(doc.id, " => ", this.m_array_history.push(doc.data()));
        });
    }
};
GameHistoryComponent = __decorate([
    Component({
        selector: 'app-game-history',
        templateUrl: './game-history.component.html',
        styleUrls: ['./game-history.component.css']
    })
], GameHistoryComponent);
export { GameHistoryComponent };
